import styled from "styled-components";
import { ReactComponent as ArrowLeft } from '../../icons/arrow-left.svg';
import { ReactComponent as ArrowRight } from '../../icons/arrow-right.svg';
import { ReactComponent as Union } from '../../icons/union.svg';
const MainWrapper = styled.section`
position: relative;
margin: 0 auto;
width: 1184px;
height: 444px;
`
const ArrowLeftIcon = styled(ArrowLeft)`
position: absolute;
left: 0px;
top: 174px;
height: 52px;
width: 52px;
`
const ArrowRightIcon = styled(ArrowRight)`
position: absolute;
right: 0px;
top: 174px;
height: 52px;
width: 52px;
`
const ReviewBlocks = styled.div`
width: 400%;
`
const ReviewBlock = styled.div`
margin: 0 auto;
width: 1184px;
min-height: 445px;
display: flex;
align-items: center;
flex-direction: column;
`
const ReviewHolder = styled.div`
width: 840px;
font-weight: 500;
font-size: 40px;
line-height: 44px;
margin-top: 50px;
text-align: center;
`
const ReviewAuthor = styled.div`
margin-top: 10px;
width: 840px;
font-weight: 400;
font-size: 15px;
margin-top: 50px;
text-align: center;
font-family: 'Roboto', sans-serif;
span{
    display: block;
    margin-top: 5px;
    font-size: 13px;
    color: #D5DBE1;
}
`
const Nav = styled.div`
margin: 0 auto;
width: 85px;
height: 16px;
display: flex;
justify-content: space-between;
`
const NavElement = styled.div`
width: 16px;
height: 16px;
border-radius: 100%;
border: 2px solid #D5DBE1;
`
const UnionIcon = styled(Union)`
width: 64px;
height: 52px;
`
const Reviews = () => {
    return (
        <MainWrapper>
            <ArrowLeftIcon />
            <ArrowRightIcon />

            <ReviewBlocks>

                <ReviewBlock>
                    <UnionIcon />
                    <ReviewHolder>
                        This platform is awesome! I've been waiting for a tool that actually enables
                        us to source candidates easily using referrals. This is the future!
                    </ReviewHolder>
                    <ReviewAuthor>
                        BRIAN BROWN
                        <br />
                        <span>UPS</span>
                    </ReviewAuthor>
                </ReviewBlock>



            </ReviewBlocks>

            <Nav>
                <NavElement />
                <NavElement />
                <NavElement />
                <NavElement />
            </Nav>
        </MainWrapper>
    )
}
export default Reviews;
