import styled from "styled-components";
import { ReactComponent as ArrowLeft } from '../../icons/arrow-left.svg';
import { ReactComponent as ArrowRight } from '../../icons/arrow-right.svg';
import { ReactComponent as Union } from '../../icons/union.svg';
const MainWrapper = styled.section`
position: relative;
margin: 0 auto;
width: 1184px;
height: 444px;
`
const ArrowLeftIcon = styled(ArrowLeft)`
position: absolute;
left: 0px;
top: 174px;
height: 52px;
width: 52px;
`
const ArrowRightIcon = styled(ArrowRight)`
position: absolute;
right: 0px;
top: 174px;
height: 52px;
width: 52px;
`
const Review = styled.div`
margin: 0 auto;
width: 100%;
min-height: 445px;
display: flex;
align-items: center;
flex-direction: column;
`
const TextHolder = styled.div`
width: 840px;
font-weight: 500;
font-size: 40px;
line-height: 44px;
margin-top: 50px;
text-align: center;
`
const UnionIcon = styled(Union)`
width: 64px;
height: 52px;
`
const Reviews = () => {
    return (
        <MainWrapper>
            <ArrowLeftIcon />
            <Review>
                <UnionIcon />
                <TextHolder>
                    This platform is awesome! I've been waiting for a tool that actually enables
                    us to source candidates easily using referrals. This is the future!

                </TextHolder>
            </Review>
            <ArrowRightIcon />
        </MainWrapper>
    )
}
export default Reviews;
